var requested = new GlideRecord('x_655585_whmg_requested_hw');
var item = new GlideRecord('sc_cat_item');
var catalog = new GlideRecord('sc_catalog');
catalog.addQuery('title', 'Warehouse');
catalog.query();
catalog.next();
item.addQuery('short_description', current.short_description);
item.addQuery('sc_catalogs', catalog.sys_id);
item.query();
item.next();

requested.newRecord();
requested.item_requested = item.sys_id;
requested.quantity = current.quantity;
gs.addInfoMessage(current.stage);
//current.stage = 'waiting_for_approval';
requested.stage = 'waiting_for_approval';

var utente = gs.getUser();
requested.applicant = utente.getName();
requested.insert();

