sys_script_include.script:
var FilterCountryItaly = Class.create();
FilterCountryItaly.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	filterCountry: function() {
		var locationList = [];
		var i=0;
		var string = '';

		var location = new GlideRecord('cmn_location');
		location.addEncodedQuery('country=Italy');
		location.query();
		while(location.next())
			{
				locationList[i] = location.sys_id.toString();
				string += locationList[i]+',';

				i++;
			}
		gs.addInfoMessage(string);
		return 'sys_idIN'+string;
	}
});
