sys_script_include.script:
var ItemChangeInWidget = Class.create();
ItemChangeInWidget.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getOrderId: function(item) {
		//var item = this.getParameter("sysparm_item");
		var wares = new GlideRecord('x_655585_whmg_wares');
		var sccat = new GlideRecord('sc_cat_item');
		sccat.addQuery('name',item);
		sccat.query();
		sccat.next();
		wares.addQuery('item',sccat.sys_id);
		wares.query();
		var orders = [];
		while(wares.next())
		{
			orders.push(wares.id.toString());
		}
		return orders;
	}
});
