sys_script_include.script:
var SelectOnlyOpenOrders = Class.create();
SelectOnlyOpenOrders.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkOrders: function() {
		var i;
		var ids = this.getParameter("sysparm_ids");
		var order = new GlideRecord('x_655585_whmg_order');
		var arr;
		for(i=0; i<ids.length; i++)
		{
			order.addQuery('sys_id', ids[i]);
			order.query();
			order.next();
			arr=order.status;
			if(order.status=='closed')
			{
				ids[i].pop();

			}
		}
		return arr;
	}
});
