sys_script_include.script:
var RequestedInclude = Class.create();
RequestedInclude.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    createRequested: function() {
		var fullName = this.getParameter("sysparm_fullName");
		var quant = this.getParameter("sysparm_quant");
		var requested = new GlideRecord('x_655585_whmg_requested_hw');
		requested.newRecord();
		requested.applicant=fullName;
		requested.quantity=quant;
		requested.insert();
		return 0;
	}
});
