sys_ui_action.script:
var selSysIds;
var sysIdList;
var indx = 0;
var numSel = 0;
var isOpen;

function closeOrder() {
	var i;
    selSysIds = g_list.getChecked();
    sysIdList = selSysIds.split(',');
	for(i=0; i<sysIdList.length; i++)
	{
		var gaCheck = new GlideAjax('SelectOnlyOpenOrders');
		gaCheck.addParam('sysparm_name', 'checkOrders');
		gaCheck.addParam('sysparm_id', sysIdList[i]);
		gaCheck.getXML(callBack);
		if(isOpen==false)
			sysIdList[i].pop();
	}
	numSel = sysIdList.length;
	var answerConfirm = confirm('Do you want to close '+ numSel +' orders?');
	if(numSel>0 && answerConfirm)
	{
		for(i=0; i<numSel; i++)
		{
			var ga = new GlideAjax('SwitchToClosed');
			ga.addParam('sysparm_name', 'closeOrder');
			ga.addParam('sysparm_id', sysIdList[i]);
			ga.getXML();
		}
		setTimeout(function(){
			this.window.location.reload();
		}, 300);
	}
	function callBack(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
		isOpen=answer;
		alert(answer+isOpen);
	}
}
